def get_active_sensors(model: str):
    """Return a flat list of metas for the given model using hardcoded PS1 data."""
    tbl = PS1_META.get(model.upper(), {})
    sensors = []
    for key, metas in tbl.items():
        for idx, m in enumerate(metas):
            sensors.append({
                "key": key,
                "idx": idx,
                "name": m.get("Name") or key,
                "unit": m.get("Unit") or None,
                "encoding": m.get("Encoding") or None,
                "calc": m.get("Calc") or None,
            })
    return sensors
